import React from 'react';
import PropTypes from 'prop-types';
import { withStyles } from '@material-ui/core/styles';
import AppBar from '@material-ui/core/AppBar';
import Tabs from '@material-ui/core/Tabs';
import NoSsr from '@material-ui/core/NoSsr';
import Tab from '@material-ui/core/Tab';
import Typography from '@material-ui/core/Typography';
import AddCampaign from "./AddCampaign";
import CampaignsList from './CamapaignsList';

function TabContainer(props) {
  return (
    <Typography component="div" style={{ padding: 8 * 3 }}>
      {props.children}
    </Typography>
  );
}

TabContainer.propTypes = {
  children: PropTypes.node.isRequired,
};

function LinkTab(props) {
  return <Tab component="a" onClick={event => event.preventDefault()} {...props} />;
}

const styles = theme => ({
  root: {
    flexGrow: 1,
    backgroundColor: theme.palette.background.paper,
  },
  heading: {
      marginBottom:40,

  }
  , tab :{
    backgroundColor: "#8884d8",
  }
});

class NavTabs extends React.Component {
  state = {
    value: 0,
  };

  handleChange = (event, value) => {
    this.setState({ value });
  };

  render() {
    const { classes } = this.props;
    const { value } = this.state;

    return (
      <NoSsr>
        <h1 className={classes.heading}>Campaigns Dashboard</h1>
        <div >
          <AppBar position="static">
            <Tabs className={classes.tab} variant="fullWidth" value={value} onChange={this.handleChange}>
              <LinkTab   label="Add Campaign" href="page1" />
              <LinkTab label="Campaigns List" href="page2" />
              
            </Tabs>
          </AppBar>
          {value === 0 && <AddCampaign/>}
          {value === 1 && <CamapaignsList/>}
          
        </div>
      </NoSsr>
    );
  }
}

NavTabs.propTypes = {
  classes: PropTypes.object.isRequired,
};

export default withStyles(styles)(NavTabs);
