import logo from './logo.svg';
import './App.css';
import React, { Component } from 'react';
import Paper from '@material-ui/core/Paper';
import Grid from '@material-ui/core/Grid';
import {
  BarChart, Bar, Cell, XAxis, YAxis, CartesianGrid, Tooltip, Legend,
} from 'recharts';
import { withStyles } from '@material-ui/core/styles';
import Select from '@material-ui/core/Select';
import MenuItem from '@material-ui/core/MenuItem';
import {
    FormControl,
    InputLabel,
    Input,
    Button,
    TextField
  } from "@material-ui/core";

const styles = theme => ({
    button: {
      margin: theme.spacing.unit
    },
    input: {
      display: "none"
    },
    container: {
        display: 'flex',
        flexWrap: 'wrap',
      },
      textField: {
        marginLeft: theme.spacing.unit,
        marginRight: theme.spacing.unit,
        width: 200,
        marginBottom:20,
      },
      dense: {
        marginTop: 19,
      },
      menu: {
        width: 200,
      },
      formControl: {
        margin: theme.spacing.unit,
        minWidth: 120,
      },
      selectEmpty: {
        marginTop: theme.spacing.unit * 2,
      },
    
  });


class Campaign extends Component {



    constructor() {
        super();
        this.state = {id: null};
     }
     handleChange = (event) => {
        this.setState({id: event.target.value});
     }
     handleSubmit = (event) => {
        
        event.preventDefault();
     }
     render() {
        return (
          <div
            style={{
              display: "flex",
              justifyContent: "center",
              margin: 20,
              padding: 20
            }}
          >
            <form style={{ width: "50%" }}>
              <h1>Add New Campaign</h1>
    
              <FormControl margin="normal" fullWidth>
                <InputLabel htmlFor="name">Campaign Name</InputLabel>
                <Input id="name" type="text" />
              </FormControl>
              <FormControl className={classes.formControl}>
                  <InputLabel shrink htmlFor="age-label-placeholder">
                    Age
                  </InputLabel>
             <Select
               // value={this.state.age}
               // onChange={this.handleChange}
                input={<Input name="country" id="country-label-placeholder" />}
                displayEmpty
                name="age"
                className={classes.selectEmpty}
            >
            <MenuItem value="">
              <em></em>
            </MenuItem>
            <MenuItem value={'EGY'}>Egypt</MenuItem>
            <MenuItem value={'USA'}>USA</MenuItem>
            
          </Select>
         
        </FormControl>
              
    
              <FormControl margin="normal" fullWidth>
                <InputLabel htmlFor="goal">Goal</InputLabel>
                <Input id="goal" type="text" />
              </FormControl>
    
              <FormControl margin="normal" fullWidth>
                <InputLabel htmlFor="category">Category</InputLabel>
                <Input id="category"  type="text" />
              </FormControl>
    
              <Button variant="contained" color="primary" size="medium">
                Add Campaign
              </Button>
            </form>
          </div>
        );
      }
}

export default withStyles(styles)(Campaign);
