import logo from './logo.svg';
import './App.css';
import React, { Component } from 'react';
import Paper from '@material-ui/core/Paper';
import Grid from '@material-ui/core/Grid';
import {
  BarChart, Bar, Cell, XAxis, YAxis, CartesianGrid, Tooltip, Legend,
} from 'recharts';

import Button from '@material-ui/core/Button';
import { withStyles } from '@material-ui/core/styles';
import PostData from "./data.json";

const styles = theme => ({
 
    button: {
      marginTop:10,
      margin: theme.spacing.unit,
    },
  });

class App extends Component {

    

  constructor(props) {
    super(props);
    this.state = {
      error: null,
      isLoaded: false,
      items: null,
      s : "out"
    };
  }
  async componentDidMount() {
    await fetch("/campaigns/", {
      method: 'POST',
      headers: {
        
        'Content-Type': 'application/json',
      },
      body: JSON.stringify(PostData)
    });
    console.log(PostData);
    console.log("----");
    fetch("/report/?dims=country,goal&fields=name,country,category")
    .then(res => res.json())
    .then(
      (result) => {
        this.setState({
          isLoaded: true,
          items: result,
          s : "in"
        
          
          
        });
        
      },
      
      (error) => {
        this.setState({
          isLoaded: "error",
          s : "error"
          
        });
      }
    )
 
  }


  render() {
    const { classes, theme } = this.props;
    const { error, isLoaded, items } = this.state;
    var egySports = 0;
    var egyTech = 0;
    var usaSports = 0;
    var usaTech = 0;
    if (error) {
      return <div>Error: {error.message}</div>;
      
    } else if (!isLoaded) {
      return <div>Loading...</div>;
    } else {

      var egypt = this.state.items.EGY;
      var usa = this.state.items.USA;
      console.log("here");
      console.log(egypt);
      console.log(usa);
      console.log(egypt.length);
      for(var i = 0; i < egypt.length ; i++){
        if(egypt[i]["category"]=="Sports"){
          console.log(egypt["category"]);
            egySports++;
        }
        else if(egypt[i]["category"]=="Technology"){
          egyTech++;
        }
      }

      for(var i = 0; i < usa.length ; i++){
        if(usa[i]["category"]=="Sports"){
           usaSports++;
        }
        else if(usa[i]["category"]=="Technology"){
          usaTech++;
        }
      }

      console.log(egySports);
      console.log(usaSports);
      var data = [
        {
          name: 'USA', Sports: usaSports, Technology: usaTech
        },
        {
          name: 'EGYPT', Sports: egySports, Technology: egyTech
        },
    
      ];
      

    }

    return (

      
      <Grid container spacing={24}>

        <Grid item xs={12}>
        <h1 className= "header">Analysis By Country And Category</h1>
     
        </Grid>
         <Grid item xs={12}>
         <BarChart
        className="plot"
        width={1000}
        height={300}
        data={data}
        margin={{
          top: 5, right: 30, left: 20, bottom: 5,
        }}
      >
        <CartesianGrid strokeDasharray="3 3" />
        <XAxis dataKey="name" />
        <YAxis />
        <Tooltip />
        <Legend />
        <Bar dataKey="Sports" fill="#8884d8" />
        <Bar dataKey="Technology" fill="#82ca9d" />
      </BarChart>

      
      <Button variant="contained" color="primary" className={classes.button}
       onClick={this.dailyPrices}
      >
        Daily
      </Button>

      <Button variant="contained" color="primary" className={classes.button}
       onClick={this.weeklyPrices}
      >
        Weekly
      </Button>

         </Grid>

      
        
    </Grid>);
  }
}

export default withStyles(styles)(App);
