import logo from './logo.svg';
import './App.css';
import React, { Component } from 'react';
import Paper from '@material-ui/core/Paper';
import Grid from '@material-ui/core/Grid';
import {
  BarChart, Bar, Cell, XAxis, YAxis, CartesianGrid, Tooltip, Legend,
} from 'recharts';
import { withStyles } from '@material-ui/core/styles';
import Select from '@material-ui/core/Select';
import MenuItem from '@material-ui/core/MenuItem';
import {
    FormControl,
    InputLabel,
    Input,
    Button,
    TextField
  } from "@material-ui/core";

const styles = theme => ({
    button: {
      margin: theme.spacing.unit
    },
    input: {
      display: "none"
    },
    container: {
        display: 'flex',
        flexWrap: 'wrap',
      },
      textField: {
        marginLeft: theme.spacing.unit,
        marginRight: theme.spacing.unit,
        width: 200,
        marginBottom:20,
      },
      dense: {
        marginTop: 19,
      },
      menu: {
        width: 200,
      },
      formControl: {
        margin: theme.spacing.unit,
        minWidth: 120,
      },
      selectEmpty: {
        marginTop: theme.spacing.unit * 2,
      },
    
  });


class Campaign extends Component {
    constructor() {
        super();
        this.state = {
            name: "",
            country:"",
            category:"",
            budget:0,
            goal:""
        
        };
     }
     handleChange = (event) => {
        this.setState({id: event.target.value});
     }
     handleSubmit = (event) => {
        
        event.preventDefault();
     }
     render() {
        const { classes } = this.props;


        return (
          <div
            style={{
              display: "flex",
              justifyContent: "center",
              margin: 20,
              padding: 20
            }}
          >
            <form style={{ width: "50%" }}>
              <h1>Add New Campaign</h1>
    
              <FormControl margin="normal" fullWidth>
                <InputLabel htmlFor="name">Campaign Name</InputLabel>
                <Input id="name" type="text" />
              </FormControl>
              <FormControl  fullWidth>
                 <InputLabel htmlFor="country-simple">Country</InputLabel>
                <Select
            //value={this.state.age}
            //onChange={this.handleChange}
            inputProps={{
              name: 'country',
              id: 'country-simple',
            }}
          >
            <MenuItem value="">
            
            </MenuItem>
            <MenuItem value="EGY">EGY</MenuItem>
            <MenuItem value="USA">USA</MenuItem>
        
          </Select>

        </FormControl>

        <FormControl  fullWidth>
                 <InputLabel htmlFor="goal-simple">Goal</InputLabel>
                <Select
            //value={this.state.age}
            //onChange={this.handleChange}
            inputProps={{
              name: 'goal',
              id: 'goal-simple',
            }}
          >
            <MenuItem value="">
              
            </MenuItem>
            <MenuItem value="conversion">Conversion</MenuItem>
            <MenuItem value="awarness">Awarness</MenuItem>
        
          </Select>

        </FormControl>
        
              
    
              <FormControl margin="normal" fullWidth>
                <InputLabel htmlFor="goal">Budget</InputLabel>
                <Input id="budget" type="Number" />
              </FormControl>
    
              <FormControl margin="normal" fullWidth>
                <InputLabel htmlFor="category">Category</InputLabel>
                <Input id="category"  type="text" />
              </FormControl>
    
              <Button variant="contained" color="primary" size="medium">
                Add Campaign
              </Button>
            </form>
          </div>
        );
      }
}

export default withStyles(styles)(Campaign);
