import './App.css';
import React, { Component } from 'react';
import { withStyles } from '@material-ui/core/styles';
import Select from '@material-ui/core/Select';
import MenuItem from '@material-ui/core/MenuItem';
import {
    FormControl,
    InputLabel,
    Input,
    Button,
    TextField
  } from "@material-ui/core";
  import Table from '@material-ui/core/Table';
  import TableBody from '@material-ui/core/TableBody';
  import TableCell from '@material-ui/core/TableCell';
  import TableHead from '@material-ui/core/TableHead';
  import TableRow from '@material-ui/core/TableRow';
  import Paper from '@material-ui/core/Paper';


const variantIcon = {
    success: CheckCircleIcon,
    warning: WarningIcon,
    error: ErrorIcon,
    info: InfoIcon,
  };

const styles = theme => ({
    button: {
      margin: theme.spacing.unit,
      marginTop:30
    },
    input: {
      display: "none"
    },
    container: {
        display: 'flex',
        flexWrap: 'wrap',
      },
      textField: {
        marginLeft: theme.spacing.unit,
        marginRight: theme.spacing.unit,
        width: 200,
        marginBottom:20,
      },
      dense: {
        marginTop: 19,
      },
      menu: {
        width: 200,
      },
      formControl: {
        margin: theme.spacing.unit,
        minWidth: 120,
      },
      selectEmpty: {
        marginTop: theme.spacing.unit * 2,
      },

      success: {
        backgroundColor: green[600],
      },
      error: {
        backgroundColor: theme.palette.error.dark,
      },
      info: {
        backgroundColor: theme.palette.primary.dark,
      },
      warning: {
        backgroundColor: amber[700],
      },
      icon: {
        fontSize: 20,
      },
      iconVariant: {
        opacity: 0.9,
        marginRight: theme.spacing.unit,
      },
      message: {
        display: 'flex',
        alignItems: 'center',
      },
      success: {
        backgroundColor: green,
      },
    
  });

  
class AddCampaign extends Component {
    constructor(props) {
        super(props);
        this.state = {
          error: null,
          isLoaded: false,
          items: null,
          cat: true,
          s : "out"
        };
    
        this.byCategory = this.byCategory.bind(this);
        this.byGoal = this.byGoal.bind(this);
      }
      async componentDidMount() {
               
        console.log(PostData);
        console.log("----");
        fetch("/report/?dims=country,goal&fields=name,country,category,goal,budget")
        .then(res => res.json())
        .then(
          (result) => {
            this.setState({
              isLoaded: true,
              items: result,
              s : "in"
            
              
              
            });
            
          },
          
          (error) => {
            this.setState({
              isLoaded: "error",
              s : "error"
              
            });
          }
        )
     
      }
    
     render() {
        const { classes } = this.props;
        const { items } = this.state;
       
        return (
            <Paper className={classes.root}>
              <Table className={classes.table}>
                <TableHead>
                  <TableRow>
                    <TableCell>Campaign</TableCell>
                    <TableCell align="right">Country</TableCell>
                    <TableCell align="right">Budget</TableCell>
                    <TableCell align="right">Goal</TableCell>
                    <TableCell align="right">category</TableCell>
                  </TableRow>
                </TableHead>
                <TableBody>
                  {items.map(row => (
                    <TableRow >
                      <TableCell component="th" scope="row">
                        {row.name}
                      </TableCell>
                      <TableCell align="right">{row.country}</TableCell>
                      <TableCell align="right">{row.budget}</TableCell>
                      <TableCell align="right">{row.goal}</TableCell>
                      <TableCell align="right">{row.category}</TableCell>
                    </TableRow>
                  ))}
                </TableBody>
              </Table>
            </Paper>
          );
      }
}

export default withStyles(styles)(AddCampaign);
