import logo from './logo.svg';
import './App.css';
import React, { Component } from 'react';
import Paper from '@material-ui/core/Paper';
import Grid from '@material-ui/core/Grid';



class App extends Component {

  constructor(props) {
    super(props);
    this.state = {
      error: null,
      isLoaded: false,
      items: null,
      s : "out"
    };
  }

  componentDidMount() {
    console.log("----");
    fetch("/report/?dims=country,goal&fields=name,country,category")

    .then(res => res.json())
    .then(
      (result) => {
        this.setState({
          isLoaded: true,
          items: result,
          s : "in"



        });
        //console.log(this.state.items);
      },

      (error) => {
        this.setState({
          isLoaded: "error",
          s : "error"

        });
      }
    )
    //console.log(this.state.s);
    //console.log(this.state.items);
  }


  render() {
    const { error, isLoaded, items } = this.state;
    var egySports = 0;
    var egyTech = 0;
    var usaSports = 0;
    var usaTech = 0;
    if (error) {
      return <div>Error: {error.message}</div>;

    } else if (!isLoaded) {
      return <div>Loading...</div>;
    } else {

      var egypt = this.state.items.EGY;
      var usa = this.state.items.USA;
      console.log("here");
      console.log(egypt);
      console.log(usa);
      console.log(egypt.length);
      for(var i = 0; i < egypt.length ; i++){
        if(egypt[i]["category"]=="Sports"){
          console.log(egypt["category"]);
            egySports++;
        }
        else if(egypt[i]["category"]=="Technology"){
          egyTech++;
        }
      }

      for(var i = 0; i < usa.length ; i++){
        if(usa[i]["category"]=="Sports"){
           usaSports++;
        }
        else if(usa[i]["category"]=="Technology"){
          usaTech++;
        }
      }

      console.log(egySports);
      console.log(usaSports);



    }

    return (
      <div className="App">
        <header className="App-header">
          <img src={logo} className="App-logo" alt="logo" />
          <p>
            Edit <code>src/App.js</code> and save to reload.
          </p>
          <a
            className="App-link"
            href="https://reactjs.org"
            target="_blank"
            rel="noopener noreferrer"
          >
            Learn React
          </a>
        </header>
      </div>
    );
  }
}

export default App;
