import logo from './logo.svg';
import './App.css';
import React, { Component } from 'react';
import Paper from '@material-ui/core/Paper';
import Grid from '@material-ui/core/Grid';
import {
  BarChart, Bar, Cell, XAxis, YAxis, CartesianGrid, Tooltip, Legend,
} from 'recharts';

import Button from '@material-ui/core/Button';
import { withStyles } from '@material-ui/core/styles';

class CategoryChart extends Component {

  constructor(props) {
    super(props);
    this.state = {
        error: null,
        isLoaded: false,
        items:[]
      
      };
    
  }
  async componentDidMount() {

    await fetch("/report/?dims=country,goal&fields=name,country,category,goal")
    .then(res => res.json())
    .then(
      (result) => {
        this.setState({
          isLoaded: true,
          items: result,
          s : "in"
        
          
          
        });
        
      },
      
      (error) => {
        this.setState({
          isLoaded: "error",
          s : "error"
          
        });
      }
    )
    
 
  }


  render() {
    const { classes, theme , campaigns } = this.props;
    const { error, isLoaded, items } = this.state;
    var egySports = 0;
    var egyTech = 0;
    var usaSports = 0;
    var usaTech = 0;
    
      var egypt = this.state.items.EGY;
      var usa = this.state.items.USA;
      for(var i = 0; i < egypt.length ; i++){
        if(egypt[i]["category"]=="Sports"){
          
            egySports++;
        }
        else if(egypt[i]["category"]=="Technology"){
          egyTech++;
        }
      }

      for(var i = 0; i < usa.length ; i++){
        if(usa[i]["category"]=="Sports"){
           usaSports++;
        }
        else if(usa[i]["category"]=="Technology"){
          usaTech++;
        }
      }

      
      var data2 = [
        {
          name: 'USA', Sports: usaSports, Technology: usaTech
        },
        {
          name: 'EGYPT', Sports: egySports, Technology: egyTech
        },
    
      ];
      

    

    return (

      
      <Grid container spacing={24}>

       
    <Grid item xs={12}>
    <BarChart
        className="plot"
        width={1000}
        height={300}
        data={data2}
        margin={{
          top: 5, right: 30, left: 20, bottom: 5,
        }}
      >
        <CartesianGrid strokeDasharray="3 3" />
        <XAxis dataKey="name" />
        <YAxis />
        <Tooltip />
        <Legend />
        <Bar dataKey="Sports" fill="#8884d8" />
        <Bar dataKey="Technology" fill="#82ca9d" />
      </BarChart>

         </Grid>

      
        
    </Grid>);
  }
}

export default CategoryChart;
